import logo from "./logo.svg";
import "./App.css";
import Days from "./components/Days";
import axios from "axios";
import React, { useState, useEffect } from "react";

function App() {
  const [date, setDate] = useState(new Date().toLocaleTimeString());
  const [data, setData] = useState();
  let [pupul, setPupul] = useState(0);
  useEffect(() => {
    console.log("Hey joe");
  }, [pupul]);
  useEffect(() => {
    console.log("Hey joe");
  }, [pupul]);
  const timeHandler = () => {
    setDate(new Date().toLocaleTimeString());
  };
  const url =
    "https://api.openweathermap.org/data/2.5/forecast?q=Yerevan&appid=ff3797c96eb489d052934ee3f65f68be";
  const getData = () => {
    axios.get(url).then(response => setData(response));
    console.log(data);
  };
  setInterval(timeHandler, 1000);
  return (
    <div>
      <h1 className="background">5-Day forecast</h1>
      <p className="location">{date}</p>
      <div className="mainBox">
        <Days date="March 1st, 1:00pm" dayName="Friay" />
        <Days date="March 1st, 1:00pm" dayName="Saturday" />
        <Days date="March 1st, 1:00pm" dayName="Sunday" />
        <Days date="March 1st, 1:00pm" dayName="Monday" />
        <Days date="March 1st, 1:00pm" dayName="Thursday" />
      </div>
      <button
        onClick={() => {
          setPupul((pupul += 1));
        }}
      >
        {pupul}
      </button>
    </div>
  );
}

export default App;
