    "Friday",
    "Saturday",
  ];
  const [data, setData] = useState();
  const url =
    "https://api.open-meteo.com/v1/forecast?latitude=40.18&longitude=44.51&daily=weathercode,temperature_2m_min&timezone=auto";
  useEffect(() => {
    axios.get(url).then(response => setData(response.data));
  }, []);
  console.log(data);
